from random import choicefrom tkinter import *choices = ["paper", "rock", "scissors"]count =0usrCount = 0cpuCount = 0def play(player, cpu):    if player == cpu:        return None    elif player == "rock" and cpu == "scissors":        return True    elif player == "rock" and cpu == "paper":        return False    elif player == "scissors" and cpu == "paper":        return True    elif player == "scissors" and cpu == "rock":        return False    elif player == "paper" and cpu == "scissors":        return False    elif player == "paper" and cpu == "rock":        return True    else:        passdef play_cmd(player):    global count    global usrCount    global cpuCount    cpu = choice(choices)    is_user_winner = play(player, cpu)    if is_user_winner is None:        text_label.config(text="cpu: " + cpu.upper() + '\n'+ "player: " + player.upper()+ '\n'+ "Tie! Try again!", fg="blue")        usrCount+=1        cpuCount+=1    elif is_user_winner:        text_label.config(text="cpu: " + cpu.upper() + '\n'+ "player: " + player.upper()+ '\n'+ "You win... Let's play again", fg="green")        usrCount+=1    else:        text_label.config(text="cpu: " + cpu.upper() + '\n'+ "player: " + player.upper()+ '\n'+ "I win, I win!", fg="red")        cpuCount+=1    count+=1    if count==10:        print('usrCount: ', usrCount, 'cpuCount: ', cpuCount)        text_label.config(text='usrCount: '+ str(usrCount) +  ' cpuCount: '+ str(cpuCount), fg="red")root = Tk()root.title("Paper, Rock, Scissors")root.geometry("400x400")text_label = Label(root, font=60, fg='navy', text="Hi! Let's play paper, rock, scissors!")text_label.pack()Button(root, text="üìÉ Paper", font=40, width=15, command= lambda: play_cmd("paper")).pack()Button(root, text="ü§ò Rock", font=40, width=15, command=  lambda: play_cmd("rock")).pack()Button(root, text="‚úÇÔ∏è Scissors", font=40, width=15, command= lambda: play_cmd("scissors")).pack()root.mainloop()